window.searchIndex = {"fields":["title","body"],"pipeline":["trimmer","stopWordFilter","stemmer"],"ref":"id","version":"0.9.5","index":{"body":{"root":{"docs":{},"df":0,"1":{"docs":{"https://alsaaleak.github.io/about/":{"tf":1.0},"https://alsaaleak.github.io/posts/qd-lst-wllm-n/":{"tf":1.0}},"df":2},"2":{"docs":{"https://alsaaleak.github.io/about/":{"tf":1.0},"https://alsaaleak.github.io/posts/qd-lst-wllm-n/":{"tf":1.0}},"df":2},"3":{"docs":{"https://alsaaleak.github.io/about/":{"tf":1.0},"https://alsaaleak.github.io/posts/qd-lst-wllm-n/":{"tf":1.0}},"df":2,"0":{"docs":{"https://alsaaleak.github.io/posts/ktb-ls-lyk/":{"tf":1.0}},"df":1}},"4":{"docs":{"https://alsaaleak.github.io/posts/khbr-lskyn/":{"tf":1.0},"https://alsaaleak.github.io/posts/qd-lst-wllm-n/":{"tf":1.0}},"df":2},"5":{"docs":{"https://alsaaleak.github.io/posts/qd-lst-wllm-n/":{"tf":1.0}},"df":1},"n":{"docs":{},"df":0,"b":{"docs":{},"df":0,"s":{"docs":{},"df":0,"p":{"docs":{"https://alsaaleak.github.io/posts/hky-hryr-lqs-l-wl/":{"tf":1.7320508075688772},"https://alsaaleak.github.io/posts/nfy-lm-b-n-l-rb-l-wy-l/":{"tf":1.0},"https://alsaaleak.github.io/posts/qd-lst-wllm-n/":{"tf":1.0}},"df":3}}}},"s":{"docs":{},"df":0,"a":{"docs":{},"df":0,"m":{"docs":{},"df":0,"e":{"docs":{"https://alsaaleak.github.io/posts/nfy-lm-b-n-l-rb-l-wy-l/":{"tf":1.0}},"df":1}}},"i":{"docs":{},"df":0,"t":{"docs":{},"df":0,"u":{"docs":{},"df":0,"a":{"docs":{},"df":0,"t":{"docs":{"https://alsaaleak.github.io/posts/nfy-lm-b-n-l-rb-l-wy-l/":{"tf":1.0}},"df":1}}}}}}}},"title":{"root":{"docs":{},"df":0}}},"documentStore":{"save":true,"docs":{"https://alsaaleak.github.io/":{"body":"","id":"https://alsaaleak.github.io/","title":"الرئيسية"},"https://alsaaleak.github.io/about/":{"body":"\n  \n    \n       \n        \n          من هم الصعاليك؟\n          \n            جماعة من العرب في عصر ما قبل الإسلام، عُرفت بمفارقة قبيلتها وغزوها القبائل بقصد الأخذ من الأغنياء وإعطاء المنبوذين أو الفقراء، وكان معظمهم من شعراء الجاهلية البارزين.\n            عُرف الصعاليك بقوة بأسهم وشجاعتهم وصبرهم وسرعتهم في العدو، حتى سموا بالعدائين، وانقسموا إلى ثلاث.\n            1- الخلعاء: ويسموا أيضا الشذاذ وهم الذين يخالفون أعراف القبيلة التي ينتمون إليها فتطردهم لكثرة جرائرهم، منهم: حاجز الأزدي، وقيس بن الحدادية، وأبي الطحان القيني. \n            2-السّود: وهم أبناء الإماء الحبشيات الذين لم يعترف بهم آبائهم ونبذوهم ولم يلحقوهم بهم لعار ولادتهم، منهم: السليك بن السلكة، وتأبط شرًّا، والشَّنْفَرى.\n            3- الملتحقون بالصعاليك: وهم الفئة التي اختارت درب الصعلكة تفرسًا، فهُم من استغلوا الصعلكة لينالوا بها مجدًا، ولعلّ أشهرهم عروة بن الورد العبسي.\n          \n        \n        \n          \n            \n            \n                  وفي الأرض منأى للكريم عن الأذى ***\nوفيها لمن خاف القلى متعزل\n          \n        \n      \n    \n    \n       \n        \n          ماذا أردنا بالصعلكة وماهي؟\n          إنها مفارقة القوم نبغي فيها إطلاق العقل إلى سعته من الحق الذاب عنه الهوى أعني هوى العرب المعاصرين الأعجمي فكانت لنا مما هم فيه من اللغط والباطل كأنا سادة هجرت لما صفونا عن كدرهم وقد جعلونا مغمورين بغير حق ولم ينصفونا وما استرحنا حتى بعد أن توارينا بالمسافة ظلوا يقنصوننا بمناظير ١٦ ضعف ورصاصات كأمثال سبابتك يجهلوننا فجَهلنا لهم ونزعنا حلمنا وصرنا مصيرنهم إلى البؤسى بدولة متوعدة مترامية الأطراف ضواحيها الأطواد وحوائطها السحب وتأجيل جيش حَسْبُكَ من الرعال يُذهب مذاهبهم طحينا نخص منها الحداثة والترميز والرمنتكية والحدث الأصغر الحر والعصيدة النثرية والبعر الشعبي والعرنجية والتخريج النحوي ومجمع القاهرة وكل ما استحدث من جنابة على الأمة أن تتطهر منها ما كان نصحا بإمساك يد قد طلعوه إلى إمساك قول الحارث بن حلزة بأيديهم رماح صدورهن القضاء\n          \n        \n        \n          \n            \n            \n                 كأن مثار النقع فوق رؤوسنا *** وأسيافنا ليل تهاوى كواكبه\n          \n        \n      \n    \n  \n\n","id":"https://alsaaleak.github.io/about/","title":"من هم الصعاليك؟"},"https://alsaaleak.github.io/posts/":{"body":"","id":"https://alsaaleak.github.io/posts/","title":"الكُنَّاشَةُ"},"https://alsaaleak.github.io/posts/fsl-fy-l-rdmn/":{"body":"‏بينما كنتُ أبحثُ في أخبارِ الشجعان، وطرائفِ الفرسان، إذ وقعَ لي خبرُ بيلوف بن يجثو الفارس الغني عن سلاحه، عزمت على أن أسأل شيخًا أعرفه أشقر يقال له: أبو جِبْت، قال: يُروَى في أخبار الملوك والفرسان، من الروس والأردمان: أنّ بيلوف بن يجثو كان فارسًا جسورًا يُهاب في بلاد الشمال، لا يعجزه خصم ولا تردعه أهوال، وما زال في قوّة السواعد وسطوة الشجعان، حتى طغت أخبارُ بلاءٍ حلّ بأحد ملوك الصقالبة والروس، يدعى رجار الذي شاد قصرًا عظيمًا سمّاهُ بدار العامة، حيث يجتمع فيه بفرسانه وأمراء جيشه ليأنسوا بالولائم والسرور، لكن غيلانَ الآجام وما تلدها الوحوش، لم تكن تترك دارًا مبنيةً إلا وجاءها شرّها، فقد جاءهم غول فظيع في جنح الظلام، يقال له: رنديل بن أمه، يأكل من يلقاه ويفتك بالصبيانِ والشيوخ، فعمّ البلاء وتفرّقت الفرسان عن القصر، وجَعَلَ أهلُ البلاد يهربون خوفًا، فلمّا بَلَغَ ذلك إلى بيلوف بن يجثو، أبحر إلى بلاد السلطان بنفر من أصحابه وكان فيهم من يشهد له بالشجاعة، وكانوا من الأردمان والصقالبة من أعوانه، فلما بلغ بيلوف تلك البلاد، أتاه رجار السلطان المجوسي وأكرمه وأحسن إليه، وسأله إن كان يستطيع قتال الغول، فأجابه بيلوف أنه عازم على مقاتلته بغير سلاح، فما احتاج الفارس يوما إلى سلاح، فلما دخل الليل تربص بيلوف بالغول، وما هي إلا أن انسلّ رنديل بن أمه حتى دخل دار العامة، ثم انقض عليه بيلوف وأمسك بيده، فما استطاع الغول أن يفرّ ولا ينجو حتى اقتلع بيلوف ذراعه وصار يئنّ من شدّة الألم، حتى خرج من الدار يعدو إلى الغابة ومات هناك، وما لبث أن جاء شر جديد من أم رنديل، وهي أشد منه بطشًا، وجاءت إلى القصر طلبا للثأر، فلمّا هجمَت على القصر أخذت أحد رجال السلطان وهربت به إلى كهفها في جوف الأرض، فلمّا علم بيلوف بذلك، لحقها ولم يتوان عن قتالها، وما هي إلا أن اشتدّ القتال بينهما فاستعان بيلوف بسيف في الكهف قديم وتمكن بعد عناء من قتلها، فلما عاد بيلوف إلى القصر استقبله السلطان رجار أيّما استقبال، وأغدق عليه الذهب والحلل، فأقام بيلوف مع رجار مدة من الزمن ينعم بالراحة بعد عناء، فاستقر الأمن في تلك الديار، ثم عاد إلى بلاده محفوفًا بما ناله من مجد وشرف بين الأقوام.","id":"https://alsaaleak.github.io/posts/fsl-fy-l-rdmn/","title":"فصل في الأردمان"},"https://alsaaleak.github.io/posts/hky-hryr-lqs-l-wl/":{"body":"\nثم إنّ هريرة -فتق\nالله ذهنها بالحكمة- قد حكت لأبنائها قصة قبل نومهم فيها من الحكمة واللهو مافيها\nفقالت: زعموا أن بلدًا تكاثرت عليه المصائب وشعبه في هم وغم وعليهم سلطان جائر لم\nتنفع معه نصيحة عالم ولا صبر صابر&nbsp; وظل هذا البلد في مصائبه وقتًا غير قصير حتى\nذاق بعضهم ذرعًا واجتمعوا ليجدوا حلاً فقال رأسهم: إن جور السلطان قد بلغ مبلغًا\nلا يردعه نصح عالم ولا يُطيقه بشر فماذا ترون؟ \nفقال أحدهم: إن\nالعالم ينكب على علمه وينأى به عما سواه فلا يضره ما أصاب الناس وكذا الزاهد\nوالمُحتال يحتال لنفسه أما البطل فيضحى بنفسه لينقذ غيره ولا أعرف وقتًا خير من\nهذا ليظهر البطل وينقذ البلاد. \nفقال له آخر: هذه\nحيلة الكسول ينتظر غيره ليبعد عنه الضر، إن انتظرتم بطلاً فلن يظهر فالبطل يظهر من\nذات البلد المستضعف لا من غيره، كما أن البطل لا ينقذ البلد فردًا بل تُنقذ البلاد\nباجتماع شعبها ووقوفهم وقفة الرجل الواحد فأرى أن نجمع الجند من بلدنا وغيرها\nونحارب السلطان فإنما يُغلب العدو بالتعاون كما غلبت الأرانب الفيل المغتلم.\nفقال له بعضهم: وكيف\nكان ذلك؟ \nقال: زعموا أن\nأرضًا من أراضي الأرانب كثرت خيراتها ووفر ماؤها وعاشت فيها الأرانب في أُلفة\nوتعاون حتى مر فيل يومًا بهذه الأرض وأهلك منهن الكثير فنادى فيهم أرنب لبيب: يا\nمعشر الأرانب اجتمعوا على العدو الجافي فالاجتماع حيلة الضعاف.\nفاستصوبوا رأيه\nواجتمعوا ليجدوا حيلة تنفعهم في مصيبتهم هذه وكان فيهم ثلاثة عُرف فيهم كمال العقل\nوسداد الرأي فقال أولهم: أرى أن نترك الأرض لذي الخرطوم ونستريح من أذاه.\nفصاحت الأرانب: ليس\nبرأي، لا يترك أرضه كلما أصابته مُصيبة إلا الجبان. \nوقال ثاني\nالأرانب: أرى نستعين بالثعلب ليحتال لنا بحيلة تُنجينا من الفيل ونجازية إن نجح. \nفقيل له: لا يا\nأخ الأرانب لا يُدفع العدو بالعدو. \nثم قال الثالث: اجتمعوا\nفإن في الاجتماع قوة ثم احفروا في الطريق هوه وغطوها بأغصان الشجر وأوراقها حتى\nيغفل عنها الفيل فيسقط ويُقال أكلت الأرانب عقل الفيل. \nفاستصوبوا مقالته\nواجتمعوا وحفروا حفرة عظيمة سقط فيها الفيل على أم رأسه وارتاحت الأرانب من أذاه\nوإنما ضربت لكم هذا المثل لتعلموا أن الضعاف قد يغلبون القوي إن لم يعجزوا وأعدوا\nالعدة واحتالوا بما يعرفون من حيل.\nفأُعجب الرهط\nبرأيه وعزموا أن يرتحلوا لبلدان أخرى يطلبون عونهم. \nفلما انتُضي نصل\nالصباح وبرز جبين الضو اجتمع الرهط وكانوا أربعة واتفقوا ألا يعودوا إلا بجمع\nيعينهم في مصيبتهم هذه وذهب الأول إلى أرض يُقال لها دعجاء فيها مجانين جُنوا فما\nانخدعوا بعقلهم فتاكون لا يخافون لومة لائم ولمّا دخل القرية لقي مجنونا يمتطي\nعصاه فقال له: السلام عليكم ورحمة الله وبركاته فقال له المجنون: وعليكم فقال له\nضيف القرية: ما منعك أن تكمل سلام؟ فقال: منعني أني لا أدري أمسلم أنت أم كافر.\nفقال له الضيف: مالك\nلا تحسن الظن، إني مُسلم أشهد ألا إله إلا الله وأن مُحمدًا رسول الله \nفقال المجنون: إيه\nوماذا تُريد منا؟\nقال الضيف: أريد\nمعونتكم على سلطاننا الجائر \nفقال له المجنون:\nاذهب لشيخنا واطلب منه هذا ولكن اعلم أننا لا نعين إلا من به جِنة \nقال الضيف: أين\nشيخكم هذا؟ \nقال المجنون: في\nالبيت الذي بجنبه شجرة ثُم ذهب يمتطي عصاه\nمشى الضيف وتوغل\nفي الأرض فوجد كُل بيوتها بجنبها شجرة ثُم وجد رجلاً ضخم الجثة طويلاً مُتمددًا\nوقال له: أين بيت شيخكم؟ فقال: بيته جنبه بيت ولم يزد عن هذا حتى مع الإلحاح في\nالسؤال. \nثم مُضى فوجد\nرُجلاً قصيرًا مُترددًا فسأله عن البيت \nفقال له: حيّاك\nالله، من أين أقبلت؟ وأين نزلت؟ ومتى وافيت؟ وهلمّ إلى البيت، بيت الشيخ في آخر\nالطريق الشجرة المقلوبة جنبه والبيت المُهدم بقربه وشيخنا على كتفه جنازة وعلى\nرأسه وسادة ويحمل في يده عُكازة كُل وجهه جروح أعمى وأبرص وأصلع لكنه أعمى البصر\nلا البصيرة جسده هالك لكن قلبه سليم وعقله حاضر حكم أرضنا فعدل وإنا لمحسودون فيه\"\nوقاطعه الضيف\nوقال: هنيئًا لكم به.\nوذهب حيث أرشده.\nفوجد الشجرة\nالمقلوبة والبيت الخرب جانبه دخل لبيت الشيخ جانبهم وقال: السلام عليكم ورحمة الله\nوبركاته&nbsp; \nفرد الشيخ: وعليكم\nالسلام ورحمة الله وبركاته \nثُم تقدم الضيف\nفوجده كما وُصف أعمى وأبرص وأصلع على كتفه جيفة حيوان لا يُعرف شخصه وعلى رأسه كيس\nأرز وكل وجهه جروح. \nقال الشيخ: من\nأنت؟ \nقال الضيف: ضيفٌ\nيطلب العون. \nقال الشيخ: رحُب\nواديك وعز ناديك.\nقال الضيف: ولا\nألّم بك ألم ولا طاف بك عدم.\nقال الشيخ: سلمك\nالله تعالى من الرهق والوهق، وعافاك الله تعالى من الوحل والزجل.\nقال الضيف:وسلمك\nالله من الشاردات والواردات، وسلمك الله بين الأعنة والأسنة.\nقال الشيخ: لا\nابتلاك الله تعالى ببلاء يعجز عنه صبرك. وأنعم عليك نعمة يعجز عنها شكرك. \nقال الضيف:وأبقاك\nما تعاقب الليل والنهار، وتناسخت الظلم والأنوار.\nقال الشيخ: ولا\nأخلاك الله من شهر تستجده وخير منه تستمده.\nوخشي الضيف أن\nيستمرا على هذا فقال: أطال الله عمرك ولا أحوجك لغيره وجعلك مسؤولا وقته وفير لا\nسائلاً وقته شحيح، إني جئت بعدما تكاثرت على أرضي المصائب وجار علينا سلطاننا\nوذقنا ذرعًا به وإني سمعت أن هذه الأرض فيها قومٌ فتاكون لا يخشون لومة لائم ولا\nيتفكرون في عواقب أفعالهم وأنا طالبٌ العون منكم فلا تردني خائبًا. \nقال الشيخ: وصلت\nغايتك لكن لن تظفر بها مالم تُكن أهلا لها فإنّا لا نعاون إلا من هو أهلٌ للمعاونة\nفابقى معنا شهرًا وإن تحملت أرضنا ونجوت بعقلك أعناك أما إن جننت فقد وطئت أرضك\nونزلت أهلك\nفقال الضيف: قواني\nالله وأدام علي غريزة العقل.\nثُم إن الضيف قد\nعاش في القرية شهرًا مرت أول أيامه بيسر ثُم تعسرت فكل يومٍ يحس أنه المجنون&nbsp; وهم\nالعقلاء ويتخبط تخبطًا ويغم قلبه ويوشك أن يُجن فلا يجد من يحادثه بعقل ولا يجد من\nالمجانين مهربًا فهذا يمتطي عصاه وهذا يُحادث نفسه وذاك يُحادث الوحوش وآخر يُحادث\nالجمادات وهؤلاء يتعاركون على أمر سخيفٍ وكلما حاول أن يفعل فعل عقلاء كأن يكتب\nقصة أو يصنع صنعة نهره أصحاب الأرض فمر هذا الشهر كأنه سنة وأوشك أن يستسلم غير\nأنه ذكر نفسه بحقده وغضبه وأكد لها أنها أيام وتمضي\nوفي اليوم التالي\nللشهر طلبه الشيخ فجاءه وقال له: ما عدت أقوى أن أُمسك زمام عقلي أكثر من هذا. \nفقال له الشيخ: حسبك\nهذا القدرُ من الصبر وإنه لأمر عظيم خليقٌ أن تفرح به وإنك إن تغلبت على نفسك فلا\nأحد يقدر عليك وإنّا مُساندوك في حربك هذه إما مُنتصرون أو مَيتون. \nثُم جمع الشيخ\nمئة من جنده وذهبوا مع الضيف لأرضه مُدججين بأسلحة عجيبة فيهم من يَحمل فأسًا حجمه\nضعف فؤوسنا المعروفة ومن يحمل ثلاث سيوف وآخر يحمل قوس سهامه سيوف وآخر يحمل سلسلة\nطويلة وكُلٌ يحمل سلاحًا مُختلف عن صاحبه. \nوحين اقترابهم\nوجدوا جيشًا آخر جلبه أخ ضيف قرية المَجانين واجتمع الجيشان فحدث ضيف قرية\nالمجانين قصته لأخيه وحدثه عن قصته فقال: \nلمّا تفرقنا لم\nأدري إلى أين أذهب وأي أرض أحل وما ظننت أن أهل أرضٍ أعرفها يُساعدوني فسرت وسرت\nحتى طرحتني النوى مطارحها إلى صحراء بعيدة وجدت فيها قومًا ليسوا كغيرهم اتخدوا\nالقفار البعيدة مَسكن لهم ونأوا بنفسهم عن القوم ورأيت فيهم قدرتهم على إنجادنا\nفدنوت منهم ولمّا صكَّ الشبح بالشبح قُلت السلام عليكم فقال رأسهم: وعليكم السلام\nورحمة الله وبركاته تفضل يا أخ العرب؟ \nفقلت: إني امرؤ\nمن أرض بعيدة علينا سلطان جائر ينُغص معيشتنا فارتأينا أن نردعه ونجمع من يعيننا\nعلى هذا فمضيت لا أدري أين أذهب حتى وصلت هنا وإني أتوسم فيكم المقدرة على إنجادنا.\n\nفقال: وماذا\nسنجني من مُساعدتكم؟ \nقلت: رحمة\nالوالدين \nفقال: وماذا\nأيضًا؟\nقلت: إن شاء الله\nوانتصرنا لكم ما شئتم من الأموال ولكني لا أظنكم ممن يبغي الأموال فأخبروني\nمُبتغاكم فأرى. \nفقال: إننا قوم\nلا نبغي إلا علو الكلمة ومجدًا مؤثلاً ونعينك إن عاهدتنا أن تصير أنت وقومك على\nمنهجنا وتنأوا بأنفسكم عن سفاسف القوم وملذات الدنيا ومُهلكاتها. \nفقلت: لكم ما\nأردتم \nفقال: بت الليلة\nمعنا وغدًا نبعث معك رسولين ليتبينا أمر أرضك وما حل بها. \nفبت معهم ليلة\nفراقها الصُبح ومُضيت مع الاثنين وعدت للأرض وعرفا أن خبري حق لا مرية فيه فعدنا\nإليهم وجئت بهم معي وهاهم هنا جُمع من الشداد العدائين الكِرام سموا أنفسهم\nالصعاليك. \nفقال له أخوه: أحسنت\nوالاختيار وظفرت بالقوم نعم القوم ثُم سأله: أما عرفت ما حل بالأخرين؟ فقال: لا؟\nفقال له: اليوم ننام ليلتنا هنا وغدًا نهجم على الأرض جاؤوا أو لم يجيئوا\nوباتوا ليلتهم\nهذه يتحارسون ولمّا انتضي نصل الصباح وبرز جناح الضو إذا بهم يرون شخصين يدنوان\nمِنهما فعند وصولهم ظهر أنهم إخوتهم فقيل لهم: ماذا حل بكم؟ وأين الجند الذين\nطلبتم معونتهم؟ \nفقالوا: إنّا\nذهبنا لقرية غير بعيدة نطلب معونتهم فأكرمونا وأحسنوا ضيافتنا وقالوا: تبقون في\nضيافتنا ثلاث أيام بلياليهم تأكلون وتشربون وتنالون ما يناله الضيف من مضيفه\nوبعدها نعينكم بعون الله ففرحنا بهذا وبقينا معهم اليوم الأول والليلة الأولى\nواليوم الثاني غير أنه في ليلتها نهض أحدنا ليقضي حاجته فسمعهم يتهامسون أنهم\nسيسلمون الضيوف للسلطان غدًا وينالون الجائزة ويتقربون من السلطان فيعين قريتهم\nهذه \nفذُهل وابتئس\nوأيقض أخوه وقال: نم تَمُت إني أهل القرية مكروا بنا ولأن بقينا هنا فإننا هالكون.\n\nفقال له أخوه وهو\nيتمتم بين حلم ويقظة: هذا ما يصيب الذي يطلب معاونة قوم لم يثبت أمرهم وقد قيل: كالمستجير\nمن الرمضاء بالنار لكننا لا نقنط ونمضي ليلتنا كأن لم نسمع شيئًا ونغافلهم غدوة\nونهرب، ثم هربنا وها نحن عندكم اثنان يجران ذيل الخذلان لم يظفران بصديق ولا مُعين.\n\nفقلنا: لا عليكم\nالمهم نجاتكم وقد ظفرنا بمن يعيننا ولله الحمد وإنا مُغيرون على أرضنا لنردي\nالسلطان قتيلاً.\nثم خطب رأسنا\nفقال: اعلموا أن الحرب لا تخاض بالسيوف وحدها بل بالعقول ولا يغرنكم كثرة الجيوش\nما أحسنتم التدبير وآمنتم بالله حق الإيمان به {كَم مِّن فِئَةٍ قَلِيلَةٍ\nغَلَبَتْ فِئَةً كَثِيرَةً بِإذْنِ اللَّهِ} ثقوا وتدبروا وكروا عليهم كرة رجل لا\nيبتغي مدح الناس بل رضى رب الناس. \nوهجمنا عليهم\nبغتة وخضنا حربًا دامية ضروس لا ينجو منها إلا طويل العمر غير أننا أُعنّا بقوم\nفتاكين لا يخافون الموت فكان الواحد يقدر على عشر من جند السلطان المتخاذل وما هي\nإلا أيام وهزم جيش السلطان ومات السلطان شر موتة ونجت الأرض من جبروته وارتاح\nالشعب منه. \nونام أبناء هريرة\nفسكتت عن الختام.\n\n","id":"https://alsaaleak.github.io/posts/hky-hryr-lqs-l-wl/","title":"حكايا هريرة - السلطان الجائر"},"https://alsaaleak.github.io/posts/jysh-lmsbh/":{"body":"بينما يتقدم ضوء بين أيديهم يسوقونه إلى المقصلة من عتبة طويلة، يريدون به غمَّا وبكاءً، ويدفعونه بحد السيف يُجَرِّحُونَ ظهرهُ حتى أن الحضور حزنوا عليه وليس بمن ذو حزن، إذ انثنى على وجهه وأقام نفسه على يديه وراح بهما يتمشى إلى المقصلة.\nفانخلع الجمهور والسائقون وقالوا: هااااااااااااااااه؟! فقال: اقطعوا رأسين بدل واحدة، عرضٌ فذ! هذه \"يهز رجله\"، وهذه \"يهز رجله\". فما أن أتموا على أفواههم من انفتاحها حتى أتاهم صوت نفض المحكمة وارتاعوا لشدته،\nفاندثر بعد هنيهة من المدخنة رجل بدراجة نار متكسرة ملتوية على بعضها، ثم اندفع إليهم ورجله ناشبة بالمحرك، فسحبها وأكمل فقال: كيفِك؟ شو أخبارِك؟ طمنيني عنك؟ وهو لا يريد به أحدا، يقوله هكذا بحكم العادة.\nفسبق ذهولهم ضوء وليس به ذهل قائلا: إنه رعد! قَدِمَ لينقذني وقد زنجته المدخنة كالرغيف المحترق! فمسح وجهه رعد وقال: اسكت، جئت لأنقذ مقصلتي. فقال له: ولأيش حاجتك بها؟ قال: أذبح الدجاج. فقال له: أوتقطع رؤوس الدجاج بمقصلة؟! إن الديك لا يبيض! فقال له رعد: بل يبيض،\nأما رأيت قول الزوزني: \"الدجاج اسم للجنس يعم ذكوره وإناثه\"؟ فالديك دجاجة تبيض. فصاحت امرأة جميلة من الناس: أنتم ماذا؟! ونحن ماذا؟! وما علاقة هذا بهذا؟! فقال لها رعد: اسكتي، لا يتكلم الزيق بحضرة النساء.\nوإذا بسمرائي غضبان: كفى! عِنّنننننننن! لو أن منخرا نفنف في منديل لأفهمني أكثر مما تُفهمِون! فراح إلى رعد مستقبضا سيفين لاعبا راقصا بهما يتقاطع حديهما حول جسمه مرارا.\nفامتهله رعد حتى وثب فما إن كاد يتلمسه وجد بوقا فوق جبينه المُتَحِّيِّر... فبوووووووووق! صَرِعَ إلى خلف وهو يمسك أذنيه آخذا رأسه يمينا وشمالا كأن في أذنيه أعشاش نملِ رصاصٍ تلدغه!\nفلم يستنشق هواءه حتى استبغته رعد وجعل يصرعه صرعا بالبوقين على أذنيه، وكل أذن تحاول الهروب بصدغها فهي سامعة: بووووووق! بووووووق! بوووووووق! بوووووووق! إلى أن طرح السمرائي نازف الأذنين متفجر الطبلتين!\nهربت الناس، وهرب الجند من خلف السمرائي، وربما المقاعد هربت، وهرب رعد معهم ثم تذكر أنه الذي هربَّهُم فأعاد بخطوه إلى المقصلة، وحملها يقول: اليوم ستعدم دجاجة أخرى. وضوء يصيح بالهاربين: جنوا نطوا! جنوا نطوا! جنوا نطوا! جنوا نطوا!\nفاتصل محمد فجاءةً على جوال رعد كأن الرنة إنذار وقال بعد الزر: أخلوا... إن أحمد قاصفكم بالباليستيات... فقال رعد: وما أخرك؟! فقال له محمد: نصَّبتُ نظام لنكس أولا... فاتصلت.\nفأسرعوا بعدها إسراع الدُّهم عن انفجار كاسح ماحق ماسح! فأعاد رعد المكالمة على محمد متعبينَ يسأله لاهثا: لم قصفنا هذا المغولي ألا لعنة الله؟! فقال أحمد وصوته مسموع: إنه كان هناك صينيون. فقال رعد: حق. وقال ضوء: أتفق. وقال محمد: بل أوشك يمضيكم إمضاءً ويلكم! وانتهت الحكاية.\n","id":"https://alsaaleak.github.io/posts/jysh-lmsbh/","title":"جيش المصباح"},"https://alsaaleak.github.io/posts/khbr-lskyn/":{"body":"ثم إنه أدام الله تمكينه، دخل على خرعوب في ليل داج، فكان يُحادِثُهُ ويحدِثُهُ فسأل: وهل علمت يا خرعوب ما قد قيل في شأن ابن البُرعُل؟\nفقال: اسمع يا عُلابط، حدثني أهلُ ثقتي عن أهل ثقتهم، عن قاسمٍ النهشلي، قال: قال: حدثني ابن رعدٍ أخو برق، صديق ابن أب حُصين، عن الأطومِ الثمثمي، قال: أبصرتُ إيليَّا أحد أيام الصيف يجلسُ في قطربل، فلما رآني لوح لي أن تعال، فأتيته وجلست، وكان عند قدَحِهِ سكينٌ حُد، فكأنه لو لمس بطناً خرج من ظهر، فقال: أعلمت يابن الثمثمي لمن هذا؟ قلتُ لا.\nقال: هذا سكين ابن البُرعُل. قلت: ومن ابن البُرعُل؟ فأشار بطرفه إلى طاولة قريبة…\nوقال: هذا الخَيتَعور ابن البُرعُل، وله من اسمه نصيب، رأيته يفري لحم كوام الإبل.\nقلت: ومن كوام الإبل؟ قال: صاحب السكين.\nقلت: أليس صاحب السكين هو ابن بُرعُل؟\nقال: لا، لعل الصفراء الكرخيَّةَ أثرت عليك. وقد كنت متأكداً أني ما دانَيتُ الصفراء، إذ إني أحب الحمراء.\nقلت: وما قصة السكين؟\nفامتعض إيليَّا وعرق جبينه ولف الراح بالراح وصرخ: يا سمك! يا سمك! هل أنت على العهد القديم مقيم؟! فصرخت السمكة في إناء من جاورنا: لا! فقال إيليا: خسف الله بك! قبحك الله من سمكة! فغضبت المغنية وقالت: لا تسُبَ السمك! فطوى إيليَّا كشحه وبصق وقال: اسمع يابن الثمثمي!\nحدثني أهل ثقتي عن أهل ثقتهم، عن عُلابط، أنه رآني ذات يومٍ في الشتاء، جالساً في سكة قريبة، أتلفح في ما ظنه فرو ذئب سميك، قد وقاني القر وبعُدَ عن العر، فإذا كوامُ الإبل يأتي يريد سلب فروتي فقلت له: يابن النهشلي! أما علمت أن الفروة في الشتاء أفضلُ من الصيف؟ قال بلى، فقلت: يا حمار..\nقل نعم، فكيف تؤكد على منفي وأنت تُقِرُ به؟ فغضب ابن النهشلي من نعتي له بالحمار، وتجاهل بديهيات اللغة، فأظهرت سكيني هذا وأشهرتُهُ في السماء، حتى كأن نصلَهُ خيطٌ من النور يَلمَع، فدُهشَ ابن النهشلي وما تركته يتكلم حتى شَجَرْتُهُ به فقُطِعَ وفار الدم…\nوما إن فرغ إيليَّا من قصته حتى سمعنا عويل ابن البُرعُل، فقلت له ما بك؟ قال: إن هذا ضرب أخي وقتله، ثم باع ثيابه حتى يتصدق على المغنية، فرد إيليا: ألا تَخرَسُ يا سكير؟ أتعبتني وأنا أقول لك أن الفري شيء، والقطع شيء، والبتر شيء، والشَّجْرَ شيء، فهلا أخرست عويلك قبل أن أُطِمَه؟\nفزَعَقَ ابن البُرعُلِ وقال: لعمري لقد طَفشتني، وما معنى الشَّجر؟ فتململ إيليَّا ورماه بالسكين فدخل في أنفه وخرج من قفاه. ثم قال: هذا هو الطعن بالسهام. فخفت على نفسي وقلت أخرج بلا صوت ولا خوت4، فخرجت وأنا اسمعه يُنشد:\nتَرَكتُ الخُمورَ لأَربابِها\nوَاقبلتُ أَشرَبُ ماءً قُراحا\nوَقَد كُنتُ حيناً بِها مُغرَماً\nكحبّ الغلامِ الفَتاةَ الرَداحا\nفَلَم يَبقَ في الصَدرِ مِن حبّها\nسِوى أَن إِذا ذُكِرت قُلتُ آحا\nوهذا ما كان من خبر السكين.\n","id":"https://alsaaleak.github.io/posts/khbr-lskyn/","title":"خبر السكين"},"https://alsaaleak.github.io/posts/ktb-ls-lyk/":{"body":"\n\n\nالفصل الأول: مئة نفر\nتذاكرت يومًا مع بِلوط بن مقنَّع (إليون ماسك) أخبار الناس والطوائف وما كان يألفهم من خيرة القوم وسؤددهم فقلت: هل حيلة في لقاء من اتفق منهم؟ فقال: حتى استأذن شيخي، فقلت ومن شيخك ولك مال الدنيا؟ فأشار على رجل ذميم الخلقة طال أنفه فلو قطعت منه النصف ما زالت منه صفة الطولفعرفت أنه يهودي، فقلت: عظيم بين الخلق وتتخذ هذا شيخًا؟ فقال هذا ما كان ثم انصرف عني في طرفة عين كأن لم يكن، فانتظرت ساعة ولم يرجع فخرجت من المجلس لحديقة بلوط فإذا هي ليست حديقته فأبصرت أرضًا لا كأرض البشر وشارفت جوا لا كجوهم، متفرع الأشجار متعطر بالأزهار وإذا ببلوط أمامي يقول: حللت أرض صفوة العرب في زمانك فبمن تريد اللقاء قلت: كلهم سواء لكنني إلى أهل اللغة والشعر أشوق، فقال لله أنت! فقلت ويعرف الله من شيخه يهودي؟ فسكت، فتجولت في تلك الأرض وكلما لقيت نفرًا سلمت عليه وحادثته بما يحب تارة وبما يكره تارة حتى إذا ختمنا حديثنا ختمت بما يحبه فيغفر لي سابق حديثي ويتبعني في تجوالي، حتى أكملنا 30 نفر، فأشرت لبلوط وقلت: أكملت حاجتي من أهل الأدب، فخذني إلى أهل المجون واللعب، فصفر حتى نزلت من السماء فرس شقراء كأنها تلتهب، وعيناه حمر كأنما الجمر، فصرنا عليه وسار بنا كالطائر يجتاب الجو فالجو ويقطع الدو فالدوحتى التمحت أرضا لا كأرضنا ولا كأرض أهل الأدب أرضها باردة لونها لون اللهب، أشجارها من ذهب وقطوفها التفاح والعنب، فقال بلوط حللت أرض المجون فمن تريد منهم؟، قلت انزل يا طالب اليهودي وكف عني لسانك، فنزلت من الفرس، وتبع أهل الأدب على تفئة، فالتمحت حانة فقلت: هلموا ندخلفدخلنا فإذا بأهل المكان سكارى فتجولت في الحانة كما تجولت في أرض الأدب ألقى سكرانا فأسايره على حد سكره وينشد البيت فأرد عليه ويرفع الكأس فأصب له، يرمي نعله فأرمي نعلي فيضحك وأضحك حتى إذا استئذنت استئذن أن يتبع ويلتحق بأهل الأدب وبمن غيره أجمع، حتى بلغت حاجتي من أهل المجون فأشرت على بلوط كما سبق فصفر كما صفر ونزلت الفرس فصرنا عليها وسارت بينا، فقال: جمعت الأدب والمجون فمن تبغي بعد، فقلت إلى أرض الملوك والساسة وأهل التاريخ والسياسة فانصرف بنا إليهم فالتمحت سماءً كسمائنا وأرضًا كأرضنا، فقال بلوط : حللت أرض الحيلة، فقلت : أدرى بالحيلة يا ابن اليهود فسكت،.فنزلت من الفرس وصار كما سبق من حديث لأهل الأرض ومشاجرة فئة ومصادقة فئة حتى أحصيت رفقتي فوجدتهم أكملوا مئة نفر، فقال بلوط: إلى أين الآن، قلت: إلى بيت أمك، فسكت وسكتُ.\n\n\nالفصل الثاني: رعد\nوحكى دوليث أن رعدا دخل يوما على بلوط وعنده العرب المعاصرون، فقال بلوط لرعد:أتعرف هؤلاء؟ قال: لا، ومن هؤلاء يا تلميذ اليهودي؟ فقال: هؤلاء العرب المعاصرون، فقالوا، نحن الذين شتمنا عرضك، وأسهرنا ليلك، وآذينا الصعاليك، فقال رعد: أما قولكم شتمنا عرضك فما ضر البحر أن يشتمه من غرق فيه، وأما قولكم وأسهرنا ليلك، فلو تركتموني أنام لكان خيرا لكم أما قولكم آذينا الصعاليك فهذا من افترائكم ويل لكم من شر اقترب ثم خرج من دار بلوط ينشدسَنُفزِعُ بَعدَ اليَأسِ مَن لا يَخافُناكَواسِعُ في أُخرى السَوامَ المُنَفَّرِيُطاعِنُ عَنها أَوَّلَ القَومِ بِالقَناوَبيضٍ خِفافٍ ذاتِ لَونٍ مُشَهَّرِفَيَوماً عَلى نَجدٍ وَغاراتِ أَهلِهاوَيَوماً بِأَرضٍ ذاتِ شَتٍّ وَعَرعَرِ\n\n\nالفصل الثالث: حِلْف\nوردنا أن بلوط بن مقنع اجتمع بحاشيته من المعاصرين يتحادثون في أمر رعد وسبيل كف شره عنهم فقال قائل منهم: لم ينفع مع الرعد الشتم ولا الهزل ولا طاقة لنا على الطعن فما عسانا نفعل؟فقال بلوط: أتعلمون ما أفضل طريقة لهزيمة عدو؟ أن تجعله حليفًا أتوني برعد ولأغرقنه بما يشتهي من مال ونساء لعل قلبه يضعف ويميل لنا فلكل قلب فجوة ولكل جواد كبوة.فقال المعاصرون بصوت واحد سمعًا وطاعة وذهب كل رهط في طريق ليبحثوا عن رعد\n\n\nالفصل الرابع: شَجَن\nمما يروى من قصص الصعاليك أن رعدًا دخل مرة على بلوط بن مقنع في داره وقال له بصوت كأنما هو زئير الأسود: سمعت أنك بعثت في طلبي فما حاجتك بي؟فأشار بلوط باصبعه إلى صندوق محشو بالذهب والجواهر حتى لم يكد يُغلق وقال له: إن مِلت لنا فهذا الصندوق لك ومثله الضعفان والثلاثةفتمعر وجه رعد وبدى من سحابه رعد وركل الصندوق بكل ما أوتي من قوة وقالأنا رعد المغدق الشامي وزير الصعاليك وخطيبهم لو جئتني بكل أموال الدنيا ما تراجعت عن خطأ واحد من أخطاء المعاصرين وتريدني أنا أغفر طوامهم كلها ثمن هذا الصندوق اعلم أن الصعاليك لا يشترون بالمال خلاف المعاصرين الغارقين في مستنقع الرأسمالية وخرج وهو ينشدنَحنُ الكِرامُ فَلا حَيّ يُعادِلُنا      مِنّا المُلوكُ وَفينا تُنصَبُ البِيَعُوَكَم قَسَرنا مِنَ الأَحياءِ كُلِّهِم      عِندَ النِهابِ وَفضلُ العِزِ يُتَّبَعُوَنَحنُ يُطعَمُ عِندَ القَحطِ مَطعَمُنا    مِنَ الشِواءِ إِذا لَم يُونسِ القَزَعُبِما تَرى الناسُ تَأتِينا سُراتُهُمُ   مِن كُلِ أَرضٍ هَويّاً ثُمَ تَصطَنِعُفَنَنحَرُ الكوم عَبطاً في أَرومَتِنا   لِلنازِلينَ إِذا ما أُنزِلوا شَبِعوافَلا تَرانا إِل حَيٍّ نُفاخِرُهُم  إِلّا اِستَفادوا فَكانوا الرَأسَ يُقتَطَعُفَمَن يُفاخِرُنا في ذاكَ نَعرِفُهُ  فَيُرجِعَ القَومَ وَالأَخبارُ تُستَمَعُإِنّا أَبَينا وَلَم يَأبى لَنا أَحَدٌ   إِنّا كَذَلِكَ عِندَ الفَخرِ نَرتَفِعُتِلكَ المَكارِمُ حُزناها مُقارَعَةً  إِذا الكِرامُ عَلى أَمثالِها اِقتَرَعوا\nالفصل الخامس: حفلة \nيحكى أن الصعاليك اجتمعوا في دار قصية في مجلس بلا سمج نفس، مُحتفلين بتحرير دمشق وجلبوا ما لذ وطاب من أكل وشراب، وأعدوا القُدور وأشعلوا النيران، وكلٌ في شأن إلا رعدًا المحتار، ينظر يمينَ شمال، ويعيد النظر مرات وكرات، كأنه يبحث عن ضالة أضاعها أو غرضٍ فقده، ولمّا يئس صاح: أين الخمور يا مفسدي السرور؟فقال له دوليث: المعصية بعد النعمة أشد من المعصية قبلها، فهاك اشرب عصيرًا وانطمر.فشرب شربة وتفل بعدها عشرًا وقال: ألا لعنة الله ما هذا الذي شربته، ويلُمِّك.فقال له: جُعِلتُ فداك، إنه عُصير مع كلُركس على كيف كيفك ينسيك الخمر وسيرته.فغضب رعد وبدا من سحابه برق ورمى الوعاء على دوليث ونهض لينقض عليه فلو أمسكه ما ترك فيه عظمًا سليمًا، لكن دوليث نادى على حصان بلوط وهرب وأُجْبِرَ البقية على تحمل غضب رعد وتهدئته، ولا احتفال ولا احتفالات وهذه عادة الصعاليك.\nفصل جانبي: الدجال \n\n\n\t\t","id":"https://alsaaleak.github.io/posts/ktb-ls-lyk/","title":"كتاب الصعاليك"},"https://alsaaleak.github.io/posts/lgrf-lhmr/":{"body":"كنت يومًا مُنكبًا على هاتفي في الظلام أُساق من موقع لموقع وأهوي من دركٍ لدرك حتى قاطعني صديقٌ يسألني حيلةً لمنع الإعلانات فقلت: نزل مانع إعلانات فقال: نزلت ولكن الإعلان ما انفك يظهر فقلت: لعل للموقع حَصانة من موانع الإعلانات فلا تبال،\nفإذا به يريني صورة تَسَعُ شاشته كُلها، كُتِب عليها: هلْ تُحب الغُرفة الحمراء؟ فقلت مُستهترًا: ما هذه الغرفة الحمراء يا رجُل؟ لعلك نزلت فيروسًا في غفلةٍ فأطفئ الحاسب وشغله، وإن اضطررت لأن تثبت النظام جديدا فافعل،\n\n\nثم تركته ويالتني ما تركت؛ فقد عرفتُ بعدها أن الغُرف الحمراء أخطَرُ أماكن الشابكة الخَفية، أي ما يسميه العامة النت المظلم حُيث تُدفع الألاف لمُشاهدة أبشع المذابحٍ وأشنع المجازر، وكان هذا مُنذ خمس سنين وتلك كانت آخر مرة أُحُادثه فيها،\nوجدته الشرطة هاربًا من المنزل وطرحت الأمر حين لم تستطع إيجاده، غير أني لا أحسبه كذلك وما هذه بعوائده.\n","id":"https://alsaaleak.github.io/posts/lgrf-lhmr/","title":"الغرفة الحمراء"},"https://alsaaleak.github.io/posts/mdhkrt-trtbys/":{"body":"\nهذا ما كان في مذكرة طرطبيس لما فتحنا بيته:\n\nالثاني والثلاثون من شهر وَعِل، سنة ١٦٣٤\nرأيت إيليا في شاذكلى متكئا عند باب ليلى، فمشيتُ حتى صرتُ إليه وسلمت وإذا به يدندن:\n\nألم تنه نفسك عَمّا بِها\nبَلى عادَها بَعضُ أَطرابها\nلِجارَتِنا إِذ رَأَت لِمَّتي\nتَقولُ لكَ الوَيلُ أَنّى بِها\n\nولم يسمعني فوقفت إلى أن قال:\n\nفَلَمّا اِلتَقَينا عَلى بابِها\nوَمَدَّت إِلَيَّ بِأَسبابِها\nبَذَلنا لَها حُكمَها عِندَنا\nوَجادَت بِحُكمي لِأُلهى بِها\nفَطَوراً تَكونُ مِهاداً لَنا\nوَطَوراً أَكونُ فَيُعلى بِها\n\nقلتُ له: ويلك! ماذا تصنع؟!\nفنظر بطرف عينه كأنهُ أعشى وقال:أنام.\nثم قال: كأنك كوام الإبل، قلتُ: لا!\nفقال: ألا أنصحك؟\nفقلت: ومن أنت؟!\nفأُرهِقَ من كلامي وجلس بعد سدحتِهِ مستقيما كالسيف وقال:\nأنا قمر الدياجي، أنا نهر العراق الثالث، أنا نيل مصر، أهرامات السودان، أنا كولوسيوم إيطاليا، أنا جوهر الشعر، أنا حافظ معلقة امرؤ القيس، أنا   شارب النجيع\nأنا المِحرَب، أنا العجيب في عيون العجائب، أنا الذي إذا رام حاجةً وقعت العوالي دونها والقواضِبُ،  أنا صاحب التاج فوق جبل قاف، أنا...\n\nفقاطعته أمُطُ حرف الألف ليسكت وقلت: يا عمي خلصنا! لقد وجعت رأسي!\nفاحمرت عيناه وضرب بيديه على فخذيه وأشار لي  وتابع:\nاصرخ فوالله لا أمرأ ولا أشهى إليَّ من بُكائك، ولا أطرَبُ إلا على صُراخِك، وما أنت لي إلا نَعجَةٌ خارت لسيخٍ دخَلَ فيها وناحت، وإني ليعجبُني عويلك وألتذ به، وهيهات أين الحصا من نجوم السماء، ما كان لإيليا أن يكافِئَهُ نعل، وما صارت هذه في القرون الأولى ولا أتت،\nوالله لا تكون...\nفضجرت من المشادة معه وطلبت نصحَهُ أخيرا فأطرق دقيقةً ثم قال:\n(لا أجدُ للمرء أفضل من التواضع)\n\nثم انسدح وهشني برجلِهِ ومشيت.\n","id":"https://alsaaleak.github.io/posts/mdhkrt-trtbys/","title":"مذكرات طرطبيس"},"https://alsaaleak.github.io/posts/mfkhr-wyndwz-wllynks/":{"body":"بسم الله الرحمن الرحيم\nالحمدلله الذي رزقنا عقلاً لنُعمله ومَلَكَاتٍ لنشحذها وجعلنا خلفاءً في هذه الأرض والصلاة والسلام على رسوله الصادق الأمين\nأما بعد:\nفإن من أعجب أحاديث الزمان التي أردنا أن نوردها في كتابنا هذا مُفاخرة الويندوز ولينكس كتبناها على قَصَصِ مفاخرة الشتاء والصيف والبقر والغنم والماء والهواء فكانت حديثًا مُستظرفًا يُخفف عن المهموم وينتفض له العصفور ولا يخْلُ من فائدة.\nثم إنه في نادٍ من نوادي التقانة لا تسمع فيه غير الحديث عن الآلة تخاصم خصمان في أمر الويندوز واللينكس الأول أخذت الآلة من عقله وشغلته بسفاسفها أما الثاني يقود الآلة ولا تقوده وهيهات بين العاجز والحازم.\nقال الأول صاحب الويندوز: إن الويندوز هو ما اعتاد الناس أن يستخدموه وهو ما تعلمه صغيرهم وكبيرهم وهو ما تستخدمه المتاجر والمصانع والشركات ومؤسسات البلاد فمن أنت لتنتقده وتزجر الناس أن يستخدموه وتُعسر عليهم وقد قال الرسول صلى الله عليه وسلم:إِنَّ الرِّفقَ لا يَكُونُ في شيءٍ إِلَّا زَانَهُ، وَلا يُنْزَعُ مِنْ شَيءٍ إِلَّا شَانَه.\nوقد قيل: الزم ما ألفت ودع ما أنكرت.\nفقال الثاني صاحب لينكس: جانبت الصواب فليس كل مألوف حسن وليس كل مغمور قبيح والناس يستخدمون ويندوز عن جهل وتعود لا عن علم ومعرفة ولو انتهى الناس عن كسلهم وتعلموا وعرفوا ما فيه من معايب لما استخدموه مرة أخرى.\nثم إن الكثرة ليست مقياسًا فالجهل فاش في الناس والكثرة مذمومة على كل حال فلو تأملت كتاب الله سبحانه وتعالى لوجدته يكثر من ذم الكثير ومدح القليل فقد قال تعالى: ﴿وَلَكِنَّ أَكْثَرَ ٱلنَّاسِ لَا يَعْلَمُونَ﴾\nوقال أيضًا:  ﴿وَقَلِيلٌ مِّنْ عِبَادِىَ ٱلشَّكُورُ﴾\nوصفوة القوم هم من يقررون أمور العامة منذ القدم والعامي ليس إلا متّبعًا\nقال صاحب الويندوز: وبماذا يزيد على ويندوز غير الصعوبة، عليك مراعاة الناس على اختلافهم فليس كل من عليها عالم بالحاسوب وخفاياه بل الأكثر عوام على قولك\nوقد كان يُقال: خاطبوا الناس على قدر عقولهم.\nقال صاحب لينكس: كلامك يظهر وهمك وقلة معرفتك فلينكس ليس الشاشة السوداء التي تتخيلها بل هو على هيئات عدة وهو ما يصح\nفيه قولك بمخاطبة الناس على قدر عقولها فمن هيئاته السهل ومنه الصعب وكلٌ تقدر أن تتعلمه غير أنك متكاسل لا تريد التعلم وقد قال الإمام الشافعي: ومـن لم يذق ذل التعـلم ســاعــة تجرع ذل الجهل طول حياته.\nوأنت بهذا حُجبت عن لينكس وكُبلت بويندوز وسجنه ومُنعت حرية لينكس.\nقال صاحب ويندوز: عن أي حرية تتحدث وأي سجن بل أنا أحر أستخدم ما يستخدمه غيري من الناس وألعاب ألعابي والحمد لله الذي أبعدني عن تنطعكم وتكلفكم فلا أستخدم برامج غريبة لم أعهدها وولست كمن يتكلف الحَلق بالموسى ولا يُحسِن فيُفسِد عمله ويعقِر صاحبه ولو كنت تعني بياناتي فأنا لا أهتم فماذا سيفعلون بها إنما يجمعونها ليحسنوا نظامهم.\nقال صاحب لينكس: إنك لا ترى قيدك فتحسب أنك حر والحمدلله الذي حررنا من قيد ويندوز وأنعم علينا بلينكس الحر مفتوح المصدر ليله كنهاره كلٌ يرى ما وراءه وليس كمثل الويندوز الخبيث الذي لا أرى له شبيهًا إلى الخمرُ المنقوع فيها السم المميت، ثم إنك لو نهضت بالعبأ وبحثت عن بدائل لبرامجك وطرق لتشغيل ألعابك الواهية لكان خيرًا من أن تجعل بياناتك تُباع تُشرى في الأسواق وكل شركة تعرف عنك ما تريد وتريك إعلانات لمنتجات لا تحتاجها.\nقال صاحب ويندوز: لست إلا متحذلقًا متفيهقًا لا تذكر لنا إلا ما لا نهتم لأمره فما هذه المعلومات التي تتحدث عنها  كأنك خازن سر الملك إنما هي معلومات يسيرة مثل أني أحب القهوة بالحليب وأقرأ رويات الخيال العلمي ،\nوذكر مؤسس شركة مايكرُسفت صاحبة ويندوز أن مشاكل الخصوصية لم تكن في البال وهم يسعون للتوسط في أمرها وقال: أن جل ابتكارات القرن لازمتها مشاكلٌ سلامة وجب إصلاحها فالسيارات أحدثت حوادثًا ومات الكثير من صواعق الكهرباء فلا مناص\nقال صاحب لينكس: هذه الأوصاف لا تعدل شروى نقير إنما تُخادع نفسك بها فنحن لسنا متفيهقين ولا متحذلقين بل جربنا كل هذا وزيادة وإن تكلمنا نتكلم بعلم ولا نهرف بما لا نعرف وقد قال تعالى: ﴿وَلَا يُنَبِّئُكَ مِثْلُ خَبِيرٍ﴾\nولا أرى نفعًا في الاستمرار معك فسأفصل لك المسألة وأنت حر بعدها، لا شك أن لينكس أفضل بناءً من ويندوز فأساسه متين ونواته أحسن من نواة نت المُتعفنة ومبني بناءً سليم خلاف ويندوز الذي يراكم الأشياء فوق بعضها ولطالمًا وجدوا حشوًا لا معنى له فيه وطريقًا\nللوصول للأشياء القديمة كما أنه مبني ليستفيد من عتادك خير استفادة ويعطيك أفضل أداء على خلاف ويندوز الذي يستنزف عتاد وكل ما حسنت يسألك زيادة ولينكس حر مفتوح المصدر لا تستأثر به شركة واحدة فتدفعك ثمنه وتسلبك معلوماتك وتتاجر بها وتُخادعك فتقول أنها لا تفعل ذلك وكلنا نعرف أنها تفعل\nكما أن أمانه أعلى من ويندوز وقلما تجد فيروسًا فيه وتقدر أن تصمم كل شيء فيه من واجهة تسجيل وسطح وقوائم وهلم جرًا وهو ليس صعبًا  كما يحسبه الكثير بل سهل ولكن بالتعلم ويمتاز بتعدد الواجهات ولك الحرية في اختيار ما أعجبك من الواجهات ومن الواجهات ما يشبه الويندوز ومنها ما يشبه الماك ومنها الذي لا يشابه أحدًا ثم لك أن تستمر فيه التعلم وتعلو إلى مراتب لن تصل إليها في ويندوز ما حييت ولا يوقفنك أنه على خلاف ما اعتدت فهذا لا يعيبه فبرامج الويندوز جلها مُغلقة وهي\nللويندوز فابحث عن بديل لها من البرامج المفتوحة وستجد بديلاً بإذن الله والسلام.\n","id":"https://alsaaleak.github.io/posts/mfkhr-wyndwz-wllynks/","title":"مفاخرة الويندوز واللينكس"},"https://alsaaleak.github.io/posts/mqm-lyl-wldhy-b-bqlm-rby-gyr-rnjy/":{"body":"كان يا مكان في قديم الزمان بُنَيَّةٌ اسمها ليلى، على هيئة الليلة، بدرٌ في حالكٍ ذي سُدُول، وهالة تلمع كالكوكب بقسماتها،كانت في يوم من الربيع قد وُصَّت وصيةً وأُمِّنَت بأمانة، قالت أمها لها: يا ليلى خذي رداءَكِ وانطلقي بهذه السلةِ طعامًا إلى جدتك، فإن المرض أتعبها، فلعل ذلك يرد لها شيئا من عافيتها، واحذري غرورا عند سبيلين إليها، طريق خطير يَؤُزُّ يُسْرُه وحسنه، وطريق طويل منفرة مشقته وشكله، وعقلك فحكِّمي وأبرمي، ولا تركني إلى هواك وكوني برة مطيعة،فقالت ليلى: إي والله، وإني أماه على ما عهدتِّه، فمضت تمرح قاطعة الطرق، حتى صارت حيث نبهتها أمها، فأخذ الشيطان بخاطرتها، وألقى لها: أن يا ليلى ذلك خير منه، وإن أمك حريصة، وماذا عساه يصيبك على قصَرِه؟فبينما تفكر صادت عينُها أزهارا قزحية على الطريق المحذور سبت فكرها، فاقتادت إليها مع حداثة حِلمها، وقالت: ما أحسن أن أغطي وجه الطعام بها كالهدية، وراحت في فيهِ تَتْبعُها قطفًا،فإذا غمَّقَت إذ خرج من شجيرة ذئب راعها وقدامها يقول: حيِّ الغزالا، أنعم بك نوالا، قالت ذُعرًا: ناولا يا طامع! قال: ويلكِ كأنني أُخِذْتُ شرًا! قالت وما تكون وأنت ذئب؟! قال أكلُّ بني آدم ينظرون إلى الصور والقلوب يحقرون؟ أذنبي أنياب ما حددتها؟أو انتسابي إلى قوم فانتسبت معي أفعالي؟قالت: بئس الخطيئة ذؤيب، كيف عُذري؟ قال: لا عتاب ولكن ما شأن السلة؟ قالت: طعام لجدتي أُوصله، قال: فامضي ليس من شيء تحذرينه قبل الدلج، فشكرته وأتبعت سيرها لآخره، فأتت الباب وقرعته مستبشرة، فأسماها صوت وقال: ادخلي،فدخلت فإذا بالذئب على السرير في زيِّ جدتها وبطنه منتفخة، فاستشكلت ودنت وأخبرت عن زيارتها وجَنَبَت سلتها،ثم قالت: أيْ جدة كيف آذانك طوال؟ قال لأسمع نغمتك! قالت وأعينك ضخمة؟ قال لأدرك وسامتك! قالت وسُنُونك شفرات؟ فأخرجَ ما كان يتكلف ترقيقه وصاتَ: لآكلك! فصاحت: ويلاه!فاهتز من الشجر لانتفاض الطيور إثر ذلك، حتى بلغ الصدى حطابا ينزع ويضرب فأسه، فانتصب وقال: طفلة!فطَفَقَ يُدَقْدِقُ دقدقة يركض ركضا والفأس بكفه، وصار من مقربة، فتمهل، فاسترق خلف سَرْحةً إلى ذئب يقول: طائران في شَرَك، طابت لمن بَرَك، فهمس الحطاب: فغشوة وغفوة، وترى يا لعين!فتأتى له وهال على رأسه بعَقْبٍ سَدَّ شخيره، فغرز حدًّا شق بطنه به، وأبرز ليلى وجدتها، قال: كيف أنتن؟ قلن: بخير جُزِيتَ كثيرا! قال: ولكُنَّ بالمثل، امضينَ وأوكلنَنِي شأنه،فرجعتا المنزل وذكرت ليلى ما كان من أول، فقالت الجدة: أي بنية، إن النفس متعلقة بمكروهين، أصغر وأكبر، فإن لم يستدفع الثاني بالأول، دُفعت النفس في غرور إلى هلاكها، قالت: أجل، لا أعيد مثلها أبدا، ولأمي عليَّ حق أن أعتذر، إنما الندم أبلغ مؤدب،أما الذئب فانتبه من نومه، وكان من ثقلين، واحد في رأسه، والآخر يجره بكرشه، يعوي عواء يلحِقُهُ بيالِ دهري، وجزاء شري، أوشكت إليه آتيا، فسبقني نائيا، ولُقِّمْتُ بدل اللحم الحجر، حُشِيَ وخُيِّط جوفي عليه فإذا حملت بعدها على الكبش عضه عن فكي الضجر.","id":"https://alsaaleak.github.io/posts/mqm-lyl-wldhy-b-bqlm-rby-gyr-rnjy/","title":"قصة ليلى والذئب بقلم عربي غير عرنجي"},"https://alsaaleak.github.io/posts/n-l-bn-l-kht/":{"body":"وقيل إن خرعوب مر يوماً بعُلابط يسألُهُ عن طرطبيس فقال: وماكان من خبرِهِ يا عُلابط؟ قال عُلابط: كُنتُ يوماً أتصيف في الصحراء معه، فدنا منا غُرابٌ كأنه قِطعَةُ ليل، فتلفت ونعق وخبط بجناحِهِ مُطيراً علينا ورقة صفراء لونها أصفى من العسل، وعليها كلامٌ بحبرٍ من زمرد…\nفما إن مسكها طرطبيس وقرأها حتى طارت عيناه، وزعق زعقَةً أرجفت ذئباً كان يتربص فخر صريعاً ! ثم اعتلى فرسَهُ فصار يطأ الليل والنهار، لا يستريح ولا يأكل، حتى وصل لبيت ابن عمه. ولما لاقاه قال: يابن عم، كنتُ أعطيتك أمانَةً خزنتها عندك، والآن أخشى تواك فهاتها…\nوكان ابن عمه مريضاً قد أُصيبَ بإسهالٍ متفجر، فلما سمع ذكر الأمانَةِ خرا، فأتت الجواري يُنظِفنَه، فلما نظفوه وأسندوه قال: اسمعني يا طرطبيس، أمانتك مسروقة في الحالتين، فهلا تركتها عندي لأن لا قِبلَ لك بصرفها. فغضب طرطبيس واهتز حُنقاً وقال: لا والله! ما سرقتُ تاج إيليا لأدعه…\nفقال ابن عمه: أطعني وخذ الوزر واسكت،\nفرد طرطبيس: بل لي المال وعليك الوزر، أنت من حرضتني! فخرا ابن عمه ثانية، فما كان من طرطبيس إلا أن ضجر فطعنه بخنجر، ففزعت جارية قريبة فطعنها وهب يخرج، ولما خرج أبصر ابن الجيران فطعنَهُ وسرق نعليه وسار…\nوفي مسيرهِ مرَّ على غارٍ منه رائحة الشواء، فدخله يطلب الطعام، وما إن وطأت رجلهُ المدخل حتى ظهر له شخصُ كائن، عيناه كالجمر، مشوه، كريه المنظر، جسمُهُ جسم خنزير ورأسُهُ رأس قرد، عيناه مشقوقتان طولاً، وكلتا يديه خطاف وسكين، ففزع طرطبيس وقال: من أنت!\nفرد عليه بصوت كالصاعقة، مسمعه يَزِنُّ السمع زنَّا: أنا أول من ناح، وأول من تغنى، وأول من حدى، فمن أنت؟ فأجاب عليه: أنا طرطبيس بن المُشَدَّخْ. فقال الكائن: وما هذا في جلبابك ويبَ أُمِك؟ قال: نعلٌ سرقته. فرد عليه: ولم سرقته؟ فأجاب طرطبيس: لا أعلم. فضحك الكائن ضحكا كالنواح وقال:تلك غريزَةُ الصعاليك، ادخل يا أرعن! فدخل طرطبيس الغار، وكان كلما اقترب اشتدت رائحة الشواء يُخالِطُها الدم، فلما وصل إلى نهايته ما رأى إلا ناراً تضمحل، فجلس عندها ثم ظهرت رؤوس سبعة حميرٍ تطير بلا جسد، أحدها كان في فيهِ رأسُ ابن عمه فتعوذ طرطبيس وطفق يركُضُ كالمجنون إلى المدخل…\nفلما خرج تعثر فسقط وأكل التراب، ثم نهض يرى ماكان وكيف سيكون، فأبصر رجُلاً عند نارٍ يُدندن:\nلَنا بِالرَدى مَوعِدٌ صادِقٌ\nوَنَيلِ المُنى واعِدٌ كاذِبُ\nفقال له: من أنت؟ فرد الرجل: صاحبُ الكأس. فرد عليه: أي كأس؟ فقال الرجل: وماذا عليك يا ملعون؟ ثم دندن:\nغَدَت تَشتَكيكَ كُؤوسُ المُدامِ\nوَيُثني عَلَيكَ القَنا وَالقُضُب\nوَكُنّا نُصانِعُ فيكَ الهُمومَ\nفَصِرنا نُصانِعُ فيكَ الطَرَب\nإِذا ما الفَتى وَصَلَ الزائِرينَ\nأَثنَوا عَلَيهِ نَأى أَو قَرُب\nثم أخرج سكينا تتلوى كالأفعَةِ لا تستقر. فقال طرطبيس:\nلماذا هي ذائبة؟ فرد الرجل: لم يعجبها وجهك. فقال طرطبيس: وما أنت فاعل بهذه؟ فقال: أقتلك لسرقَتِكَ نعل ابن أختي. فأراد الهرب فمنَعَهُ صِلٌ كان عن يمينه، فالتفت إلى يساره فرأى عجوزاً حيزبونة، فقال: امشي يا دردبيس! فردت: لا أمشي! فتعجب طرطبيس وقال: ولم؟!\nفضحكت العجوز وشخرت، وأخرجت من دِرعها أرنباً وسكيناً صدِئة فنحرته، فسال دمه على رجلها فقفزت وقفز الصل على إست طرطبيس ولدغها، فصرخ طرطبيس: أمي! أمي! فصكتهُ العجوز وقالت ما تريد¿ فقال: ماهذا؟ فردت الأفعى: كانت غدرة، أين التاج؟ فقال طرطبيس: لا أفهم. فقالت العجوز: أمرنا أن نرجع التاج\nفحزن طرطبيس وتأوه وقال سرقه ابن عمي مني، فقالت العجوز: ويك! أما اطلعت على مثبتة إيليا وقرأت: “فأنا دافعت العيان، وكابرت الإنس والجان، فكنت لكل جبار إمام” ؟ ها نحن الإنس والجان، وأنا أمك، وذاك جبار الأرض، وأشارت إلى الأفعى وقالت تلك سيدة الأفاعي شُجاعة…\nثم إن أم طرطبيس سلخت جلده وعلقته على صخرة يتخطَفُهُ الطير. وقيل إن شجاعة قطعت أذنه لتتقرب بها لملكٍ من ملوك الجن، أما ذاك الرجل فلا يُعلم من هو، لكن بعضهم قال أنه أخذ نعل ابن أخته ومشى به…\nوضجر خرعوب فقال: ما هذا الهراء! فصاح به علابط: أكلامي هراء! وقذفهُ بنعل ابن أخته.\n","id":"https://alsaaleak.github.io/posts/n-l-bn-l-kht/","title":"نعل ابن الأخت"},"https://alsaaleak.github.io/posts/nfy-lm-b-n-l-rb-l-wy-l/":{"body":"\n&nbsp;بِسْمِ اللَّـهِ الرَّحْمَـٰنِ الرَّحِيمِ\nلا ذنب للعرب القدماء بتقصيرنا في لسانهم، كانوا رعاة العربية يوم كانوا لا يتركون لها خللًا مُتَرَدّمًا ينقص كمالها، وكانوا يقومون على كل لازمةٍ لا يعيقون بها عملهم بها، وذلك ضروب متشعبة تنفصل على أسس ما يقوم عليه كل لسان أمة، فمن أهم أركانه اللفظ، والأسلوب.أما اللفظ: كانوا إذا وجدوا شيئًا جديدًا سموه بسهولةٍ لاقتدار لغتهم واقتدارهم عليها، محسنينَ وصفَهُ مبدعينَ في معناه محكمينَ له، ولا تكون عندهم بلادةٌ أو نقصٌ في التسمية، وهذا مفقود عندنا حتى في المجامع، إذ لا تحسن ولو ربعَهُ، ولا يعرفه من لا يعلمه، وجَرَّاهُ طَعَنَ الطاعنون في العربية بالقصور عن جهلٍ منهم، وعمن يحاولون دفع جهلهم من المسيئين الذين لم يخدموا العربية حق خدمتها، ولو سَرَّكَ أن تطَّلع على أَسَى الحال أشير إليك بمنظومة إبراهيم بن حافظ هذه:\n\n\n\nكيف ركن اللفظ عند العرب القدماء كاملٌ وعندنا ناقص\nإن العربي القديم كان يفتح مصراعيه على كل العربية، في أوزانها وألفاظها وكلماتها ولا يمنع نفسه شيئًا ولا يكرهه، وقيل فيه: إنه معجم سائر، واللفظ في العربية -كما لا يخفى- متقلبٌ كثيرُ التصرف، ومعانيه العددُ العديد تأتي الصفحاتِ الكبيرة مدَدًا وتملأها، فالعربية أوسع الألسنة مذاهبًا مع قدرتها على التصريف والاشتقاق، والعرب عندها مناهجُ كثيرة وسبلٌ لنعت الشيء: كالمجاز والتشبيه والتقريب والنحت والاقتباس، ولدى العربي القديم قريحةٌ قويةٌ وفصاحة، وسجيةٌ لينة، وذوقٌ يعصمه من الزلل إلى وحلِ المعاني الأعجمية الغريبة على طباع العرب، فهذا عاميهم، فكيف عامينا؟ قد أقسم لك بالله أن عوامنا يكاد لا يعرف أحدٌ منهم معنى الأطلال، ولا حال خواصنا بخير منهم بل هم شر؛ إذ يضيقون على أنفسهم العربية في أعظم أركانها وأسمن دعائمها: أماتوا الألفاظ وأوزانها ومعانيها واقتصروا على البخس منها الذي لا يغني شيئًا وقتلوا سعة العربية وقدرتها، وإذا أحدٌ حاول أن ينتهل من لفظ القدماء وكَلِمِهم، علّقوه بالتحذلق وأنه يتشدق والتغريب، فمن أين لك أن تجاري الأمم في تنسيق الأسماء للعلوم والاصطلاح ووعاؤك ضيق؟ وأي عربيةٍ هذه التي تكلمنا عن سموها ولا تعمل بها؟\nإحسان العوام وإساءتهم\nفي الناس -لمن بصر ولم يغب- بقيةٌ من كلام العرب وذوقهم في البيان، ولا أعرف برجلٍ ينكرُ شَبَهَ العاميات من الفصحى ويدعي مجانسةَ اللغة المعاصرة لكلام العرب إلا أنه مغشي، فلدنوِّ قَدَمِ العامة من حديث أسلافهم اخترعوا ألفاظًا احتاجوها لأغراضٍ صدفت عصرهم، أجادوها وفضلت على أشباهٍ أرادتها المجامع، كقولهم: “طَيَّارَة” على وزن مبالغة لتَصَعُّدِها في السماء وعَظِمِها وطول قطعِها، وتسميها المجامع “طائرة”، فلو رأوا من يقول “طيارة” في كتاب وغيره، لاستهجنوها كعادتهم مع ما يقوله العامة بغير وجه حق، وكقول المغربيين “مِصْدَاح” يريدون به المكرفون، والصَّدْح في اللسان: “صَدَحَ الرجلُ يَصْدَحُ صَدْحاً وصُداحاً، وهو صَدَّاحٌ وصَدُوحٌ وصَيْدَحٌ: رفع صوته بغناءٍ أَو غيره” وهو عمله، أما المجامع تسميه لاقطًا للَقْطِهِ الصوت، وهي تسميةٌ سمجةٌ غثة.\nما تشتقه العامة لتعبيرِ الكلام وتولِّدُه\nقََلَّ أن تجد للغة المعاصرة ألفاظًا مولودة؛ فما هي كالفصحى ولا عندها آلاتها ولا أهلها لتفعل ذلك، وقد استعجمت طباع المُجرِين كلامهم بها؛ فما زادوا لغتنا -أعني أصحاب المجامع- سوى الكلمات الرديئة باستساغتهم الهجنة نحو: شخصية وتفاعلية وإمكانية وهوية وثقافة ودِمُقْراطية وتنمية وميتافيزيقيا وأنطلوجية ومثالية وغيرها، وإنَّ العامةَ ما زال لديها اشتقاقٌ وتوليدٌ لأنهم عربٌ كما ذكرت، فما زالوا يتكلمون ولوازمهم لتفسير المعاني جاريةٌ إلى امتداد كلامهم وهي كثيرة، نحو قول أهل الشام: نفذنا من النفوذ، اشتقوها للنجاة من المأزق المُلِمّ، شبهَ قوله تعالى﴿يَا مَعْشَرَ الْجِنِّ وَالْإِنسِ إِنِ اسْتَطَعْتُمْ أَن تَنفُذُوا مِنْ أَقْطَارِ السَّمَاوَاتِ وَالْأَرْضِ فَانفُذُوا ۚ لَا تَنفُذُونَ إِلَّا بِسُلْطَانٍ﴾ويقول المصريون: خَضَّنِي كنايةً عن المفاجأة، والعرب عندها اهتزاز النفس والجسد والطرب، كل هذا حين تحل الكوارث والمصائب فتبعث النفس وتحركها، وقول الشاميين أيضًا: هذا ليس ثوبنا، أي ليس ما نكون عليه من حال وصفة كأنه معنى الهوية الغربي، والعرب تكني بالثوب عن النفس والقلب وتعلق الصفات بهِ يقال: ثوبُ صدقٍ وثوبُ غدرٍ وثوبُ نعيمٍ وثوبُ صبر، فهو من ذاك، وبعد هذا كله إنما منع العامة من استيفاء وإيعاب معاني العصر ملابستهم الألفاظ الهجينة والأعجمية، وضعف عاميتهم وقرائحهم، وأنه ليس علماءٌ يعينونهم.إن اتهامك العربية بالقصور بمجاراة العلوم إنما هو قصورك فيها، ثم هو جهلٌ ولست مع هذا الجهل مُجازًا لتحتم عليها بهذا القول وإن كنت خبيرًا بهذه العلوم المُنْفَكَّة عنها.أما الأسلوب: فهو التصاريفُ التي يجري الكلام بها ويتشكل عليها فلا يستوي -وهذا مثلٌ جلي- من قال:أَبُدِّلَ اللَّيلُ لَا تَسْرَي كَوَاكِبُهُأَم طَالَ حَتَّى حَسِبتُ النَّجمَ حَيرَانَابمن قال: هل تم تبديل الليل وتوقفت نجومه عن الحركة، أم أن الأمر استغرق وقتًا طويلًا حتى ظننت أن النجم مرتبك؟فالأول أسلوبٌ عربي والثاني أسلوب أعجمي والمعنى واحد، كذلك في هذا العصر نشأت معانٍ في الأقوام معاصرةٌ تحتاج إلى أساليب تحويها بإيجازٍ من غير إخلالٍ بأذواقهم ولا مخالفةٍ لأمزجتهم في القول، وهذا كان عندنا العرب فتصرفت فيها اللهجات، ومن حلو ذلك ما قالوا: “الحال من بعضه” يقابل قول العجم “We are in the same situation”، وقد كان عند العرب ما يشبهه ولكن العامة ابتدعت أسلوبًا بحلةٍ مستجدةٍ ذات معنى خاص، وشاهد جوازه قول طرفة:أَبَا مُنْذِرٍ أَفْنَيتَ فَاسْتَبْقِ بَعْضَنَاحَنَانِيكَ بَعْضُ الشَّرِّ أَهْونُ مِنْ بَعْضِجعل الشر أبعاضًا, وهذا جعل حالَهُ حالَ أخيه فهو بعضه، وكذا قولهم: أسوأ من بعضه، ونحوه قولهم: “مثلي مثلك” وقوله تعالى: {فَمَثَلُهُ كَمَثَلِ الْكَلْبِ إِن تَحْمِلْ عَلَيْهِ يَلْهَثْ أَوْ تَتْرُكْهُ يَلْهَث} وهذا أسلوبٌ لهم في المراهنةِ يقولون: من كم إلى كم؟ سؤالًا بعد العرض، ويستفتحون به العرض أيضا: من كم إلى كم أني أقدر أغلبه؟ وقبوله يعود إلى تجاوز العرب في كلامها وتساهلها معه، فقد ينزلون الحرف منزلة الاسم في “أَزَلَيٌّ” وأصله لم يزل، فكذلك نُنَوِّنُ (كم) تنوينَ كسرٍ وعند الوقف تكون (كم) الثانيةُ ساكنة، والعامة في الأصل تقول: من كم لكم، و (إلى) أجود من اللام أو هي الصحيحة، وأستجيد أسلوبهم: “ما عليك زود”، يقوله المُخَاطَب إلى المُخَاطِب ليستَكِفَّهُ عن السلام والتحية فيباشران الحديث المُراد، وهو معنًى خاص لم يسبق إليه، ولو جعلناه في الفصحى أراه على أن يكون: ما عليكَ فوقَه، والعرب تستعمل كلمة (فوق) في الزيادة على الكلام وغيره.على ما ترى: هذا تعبير العامة الذي تتصرف به وعذرًا عن ضيق الأمثلة، وهو مستعانٌ بِهِ عظيم، وبابٌ لا يستخف بأمره، وكلام العامةِ في أحوالٍ دليلٌ على كيف كان كلامُ العربِ ليلابسَ المعاني وينظمها بأدواته، وبئسَ من حقَّر اللهجات وعظَّمَ لغةً معاصرةً ركيكةً لا طعمَ لها ولا صبغةً وليست البَتَّةَ من ثوبنا.أنبهك إلى أنه ليس كل اشتقاق تشتقه العوام من أسلوب أو لفظ صالحٌ لكلام العرب ويلائمه، وليس كلٌّ أهلا ليعتمل في هذا، انتهى والسلام عليكم.\n\t\t","id":"https://alsaaleak.github.io/posts/nfy-lm-b-n-l-rb-l-wy-l/","title":"نفي المعابة عن العرب الأولين"},"https://alsaaleak.github.io/posts/qd-lst-wllm-n/":{"body":"\nالفصل الأول:الزرداب\nكان طفل في تبعة أطلال1 لأذكار يحدثها بها أبوه عن قوم أزليين فما زال يمشي بين العشب وصخورٍ كالمرمر2 مرقطة3 منها وسعُ الكفين وخلق الجمل وقريب عليهما إلى أن تأتى لزرداب عند بقعة بنية اللون يخالطها قليل حُجيرات ففتحه فالهباء4 يطيح على أول درجه وإذا طلع رأس شيخ بين الهباء حتى بان نصفه فقال: كم مضى من عهد عباس؟قال الطفل: ما عباس؟قال: ما أنت؟فقال الطفل: معاصرفقال ذلك الشيخ: ألا لعنة الله ثم تخلف مغلقا الزرداب5 إلى حيث مدفنه المُوَلِّي زمنُه متخلفا ما هو بمتحضرفأنشد الطفل:أبلغ عزيزًا فى ثنايا القلب منزلهُ  أني وإن كنت لا ألقاهُ ألقاهُ  وإن طرفي موصولٌ برؤيتهِ      وإن تباعد عن سكنايَ سكناهُفصاح الرجل من حجرته قاعدا على مقعد كذبت في ظنك بل القائل هو من قال: أَتى الزَمانَ بَنوهُ في شَبيبَتِهِ      فَسَرَّهُم وَأَتَيناهُ عَلى الهَرَمِ فإن كان هذا في عصره فأين أنا وبين من أكون؟وكان رعد\nالأطلال: ما بقي من آثار الديار الماضي  ↩︎المرمر: حجر شديد البياض ↩︎ والمرقط المنقوط بنقاط سوداء كبيرة أو صغيرة ↩︎الهباء: ما يتوضح من الغبار في الضوء ↩︎الزرداب:السرداب ↩︎\nالفصل الثاني:المكتبة\nالطفل: واها، مكذوبة كانت؟رعد: هاه؟ أكلمة تلذذ وتعجب؟ ما لك أنت؟الطفل: ولا الصغير يستقيم عقله في هذا الزمان.رعد: هاه.الطفل: ذا تثاؤب؟رعد: أعيده لك؟الطفل: أذلك أنك لا تحفل؟رعد: فطين، على أنك لا تعلم شيئا من غوامض هذا العصر المضحكات المبكيات.الطفل: دعك، ألا أصحبك؟رعد: كأني مسرور بحوارك، إلى أين؟الطفل: ارحمني من لسانك، إلى رؤيةٍ لهذا العصر.رعد: أعلمه.الطفل: أطلعك على مزيد.رعد: أتلك معرفة تبتغى؟الطفل: أما وقع فيك إصلاحهم؟ وترى في نبرة السؤال استنكارا.رعد: ويحك بلى، وأنت يا حلو، كيف كان عليك؟الطفل: كان شر البلية يضحك.رعد: والبلوى تبعث علي جنونا وفرط تحرك أحيانا، بلى يا وُلَيد.الطفل:وَيَضْحَكُ سِنُّ المَرْءِ والقَلْبُ مُوْجَعٌرعد: ذلك تيسير على قلبه منه. استظرفك رعد يا عفريت، فامضِ متشرفا.الطفل: شكرا لله.فَعَلِّم ما اِستَطَعتَ لَعَلَّ جيلاًسَيَأتي يُحدِثُ العَجَبَ العُجابارعد:وذا الذَّمِّ فاذمُمهُ وذا الَحمدِ فاحمَدِالطفل: ههههههههههههه، أما تترك سبهم؟رعد: أجبت نفسك. هذه خطوة، فخذ الأخرى.الطفل: إليك سؤالا بين مشينا، ما نكتة الاستنعات بالشيخ وأنت ذو عشرين؟رعد: شخت فيهم ولم يفعلوا، وبعضه من أذى العيش بينهم.الطفل: فذو السبعين، كيف؟رعد: ذلك خالد إن يكن صحيحا.الطفل: عجبا..رعد: ما عرض علي هذه الساعة؟الطفل: ما هو؟رعد: أتراها من هذه الطلعة مملكةً تنشئها جماعة؟الطفل: به! ما يريدون منها؟الحارث: هَيَا رعدا! ألا تنتحل إلينا!”الطفل: من هذا؟رعد: الحارث… كانوا قومي إن أستطع إليهم، حتى ذاك لي شؤون أعمل عليها…الطفل: إن لهم قصدا حسنا…رعد: أتْبِع إلى&nbsp; المعاصرين..الطفل: ها نحن عند المعاصرين.رعد: أذلك بناؤهم؟الطفل: كيف تراه؟رعد: صدأً طويلا، لا ريح له إلا أذاه، ولا روح فيه ولا نَفَس.الطفل: ماذا تريد بذلك؟رعد: ذلكم خلق الله الذي فطر الإنسان وطبعه عليه، أذهبوه ورفعوا الحديد من مبطن الأرض إلى السماء، ألا إن الإنسان المعاصر سفيه.الطفل: الإنسان المعاصر تقول؟ وبناء الأقدمين؟رعد: ذلك خلق الله الذي أُعمِل وشُيِّد ولم يُفنِ شيئا.الطفل: عجبت لقولك ولم أنفر.رعد: أول الحق مستغرب.الطفل: إيه؟ كيف تريد أن ترسل رسائلك وآثارك فيهم؟رعد: لا أدري بعد.الطفل: فلو أخذتك إلى مكتبة لتعتمل كلامك، ومنها يجري منطقك؟رعد: أن يمضغ حجر أسناني في فمي ويختنق آخر في حلقي… خير من كسب منكم.الطفل: ويحك! إنك لمتطرف!رعد: ويلُمِّك! هذه أظافيري أين التطريف؟!الطفل: قصدت أنك مغال!رعد: غلا لدودك بفرحه! ورخص ودودك بحزنه!الطفل: إني طفل…رعد: وجحش.الطفل: بالله اسمع مني.رعد: أنظرني في مرادك، سُقني إلى هناك.الطفل: نعم..رعد: أهذه المكتبة؟الطفل: لندخل.رعد: هذا حسن، لا صدأ، والخشب جديده كقديمه، والكتب كذلك.الطفل: وهي مكتبة عظمى على ما ترى من طول نواحيها وآلات الكتابة المنضودة للكتاب وكراسي الخطاطين إلى آخر ذلك كله… مهلا… من ذا؟ أتعرفه؟رعد: يا أعجم يا أشقر يا مستشرق… ما أنت فاعل؟الأعجمي: أوه! في الحقيقة أنا أقوم بانتقاء بعض الدواوين لأدرس الأدب العربي لأبحاث جامعة الأدب العالمية الخاصة بي…رعد: فتنتحل انتحال الناقد، وتتموضع موضع المُقَيِّم الواثق بنظره، وتتمادى جريئا فتقول لقومك، إننا نراهم كذا وكذا، ولم يكن في أدبهم كيت وكيت؛ فلا تأملوا منهم أن يكونوا مختلفين عن غيرهم وأن يكون أدبهم فوق مكانه، ولهذا ما له حضور في محاضر آداب الأمم فإذ هو قديم لا يتجدد… وأنت على كل كلامك ذلك لم تصل مواصل عامينا بقرائته وفهم طبعه لكلام جدوده، ولكنك تقرأ من الترجمات التي لا تنقل شيئا من المعنى على هيئته؛ لوهن لسانك على لساننا، ثم ألم تعرف يا جهول بأن الشعر والنثر عند كل قوم لغة خاصة: لا يمكن مشابهتها ويستحيل أن تخرج الترجمة معانيها كما ولدتها على أدنى قدر، فكيف بالعربية أخص لغة؟ هذا والطباع تختلف وذكرت أن طبعك أعجمي، فأنى لك أن تسامي بحالك المزرية هذه كلام أهل علمنا واستبصارهم وخبرتهم في أدبنا؟ وإنك يا مغفل من أسفه من حملت قوائم… دونك ودون ما تروم اجتياز الفُلُك: إنه أن تتعلم العربية حتى تتقنها إتقان نحوي عالم وتلفظها لفظ مجود، وتطوف على أدبنا مديدا وعديدا وسنينا ليتحصل لديك شيء من طبعنا، ثم أن تزاول مزاولا مزاولة علم البلاغة حافظا وفاهما، ثم أن تستفتح على معالجة الشعر وتذوقه، ومطالعة ومدارسة ومراوضة كتب نقده واستدراك مقاصدها مقصدا مقصدا، هذا أمر الشعر على دهر يتبعه دهر أمامه دهر، فإذا جمعنا بكل ما سلف من العلوم الواجبة، كان الطريق أضعاف عمرك، أفهل وعيت لوضاعة قدمك؟الموظفة: انتما ما لكما؟!الطفل: لك الويلات يا رعد على هذه المداعسة! إنها موظفة المكتبة فاكفف آلتك رجوتك!الأعجمي: بصراحة لا يتم فهمك من قبلي.رعد: خذ أيها المستشرق هذه الصحيفة وادرسها متدبرا، وانصرف راشدا قطع الله لسانك؛ فإن شيئا من الغيب على الإنس يحرك ذراعي لتصفعك صفعة تروح بحنكك ملويا.الطفل: به يا رعد؟!الأعجمي: حسنا! شكرا لك! قم بالهدوء! سأقوم بأخذها والانصراف فورا!الموظفة: أيا أهوج من أين أتيتنا أنت! من أذن لك بطرده؟! المكتبة ملك أبيك؟!الطفل: لقد ارتكبنا مقاتلنا يا رعد! لقد ارتكبنا مقاتلنا!رعد: مه يا نائح. يا امرأة، اسمي رعد مغدق، ولي جن اسمه صعق محرق، يوسوس لي، وإنه والله لو هب عليك لجعلك متبلا، وتعرفين حب الدمشقيين للمتبل، وكان عبد المجيد صديقي ونديمي على دمكم يقول: “أشهى الدم عندك دم العرنجي.” فاقعدي وإلا ثكلتِ أسرتك بعد أن تثكلكِ أمك.الموظفة: أعوذ بالله ما هذه العيون! عاااااااللااااااااالااللالاه!الطفل: أرعت روعها قاتلك ربي!رعد: لا تحزني يا أختي، غردي غردي يا عصفورة! ولا يكن وجهكِ كالبندورة!الطفل: أهذا جنونك الذي عنيت! والصحيفة! تلك الصحيفة! ماذا كتبت له فيها مصيبةً علينا؟! قل قل!رعد: إيه؟ كتبت له بيت أبي تمام:أَبْقَتْ بَنِي الأَصْفَرِ المِمْرَاضِ كَاِسْمِهِمِصُفْرَ الوُجُوهِ وَجَلَّتْ أَوْجُهَ العَرَبِوهذا ماكان من خبر رعد والمكتبة\n\t\t","id":"https://alsaaleak.github.io/posts/qd-lst-wllm-n/","title":"عقدة السطع واللمعان"},"https://alsaaleak.github.io/posts/qs-by-lthqb/":{"body":"يحكى أنه في شتاء كالح ورياح تغرز في العظام كانت طفلة جائلة تهيم على الطرق تبغي اصطياد حاجتها من وأج لها سلة شبه الدلو تبيع بها الثُقُب من أول العشيةوذات ليلةِ أذى يطلي صقيعها الممشى وكأن الشمال بها تسايف الجنوب بُعِثت بينهما مضروبةً من أبويها تحمل خاطرها وتجر حافيتين ضاعت كسوتهما كاشفةَ الرأس وعلى شعرها الأشقر مثل السكر المطحون من الثلجتروح ترجيًّا فما وقفت بشخص إلى آخر وهي تدور وتدعو إلى قُدَّامها حتى فُضَّ الشارع وأمثاله وهي حابسة تخاف الضرب إن ترجِعْ قابضةً من البرد لا على كسبوالمأوى قد بعُدَ ويئِسَ الأمل إليه وهي تمر بالبيوت والنوافذ تنظر في الزجاج فترى كل الأطفال راحوا ليفتحوا هدايا العيد بين أهاليهم فَرِحين بهمفتولت ثم اتقت بين منزلين قعدت إلى حائط وكل كفها وجع وبرد على أن رجفان أعضائها قد سكن فاشتهت دفئا وتخفيفا فأشعلت ثقابا وإذا وجدت مدفئة مضيئة حارة وملابس قطن ممدة وملحفة عريضة أمامها مدت ذراعها لتدنو فانطفؤوا بسرعة مع النويرةفرفعت غيرها فجَلَت مائدةً عليها دجاجة فوّارة تُعَلُّ مرقا وخبزا أحمر سُمسُميا ساخنا وإبريق ماء نقي فنُفِخوافكشفت بأخرى عن شجرة عيد شخصها كالجبل من أدنى وتتلألأ تلألؤًا وثلاث بنات يحملن هديات يدفعنها إليها ويقُلن هيَّكِ يا سراء لنلعبَ بها ففاضت سرورا على فمها وتاقت توقا قديما وحَرَّكَت في تعب لتجيبهن فخطفن منها وعلقت تغرغر عينها وتتعبسوما تزال تختلف الصور رغيبة العين بكل شيء وما عندها شيء حتى لمست بضع أعواد فقبصت واحدا وبصرها عند الكواكب وإذ عاينت شهابا خاطًّا قالت في نفسها إن أحدا مات جدتي قالت لي أنه إن وقع نجم من السماء فإن روحا تترفع لتخْلُصفمما فيها من حنين خيَّلَتها بذاك العود وراحت حين ما رأتها تصيح لها جدتي جدتي! فقالت لها ما جرى عليكِ بُنَيَّتِي؟قالت خذيني خذيني إليكِ قبل أن ينطفئ العود لا ترحلي كما ذهبت المدفئة والدجاجة والبنات وتتركيني وعطفت تشعل أعوادها بالجدار لتستطيلهافدنت منها وخفَضت جناحيها عليها وسمت بها نحو السماء فاسودَّ آخر ثقابوتلا الصبح فهي بين ثلج تُعاكسُه حمرةُ خديها وثغرها باسم يترحم عليها مجمعٌ ناظرين إليها ويقولون أهًّا أهًّا وا طفلة مسكينة كانت تشتهي دفئا وهم في عَمَهٍ عن أنها بما غنمت واستراحتِها كأنما نعمت بأطيب عيش ولا برد ولا بؤس","id":"https://alsaaleak.github.io/posts/qs-by-lthqb/","title":"قصة بائعة الثقب"},"https://alsaaleak.github.io/webring/":{"body":"\n    حلقة الصعاليك\nهي حلقة تجمع مواقع الصعاليك على الشابكة  ليسهل العثور عليها \n\n\n\n\n","id":"https://alsaaleak.github.io/webring/","title":"حلقة الصعاليك"}},"docInfo":{"https://alsaaleak.github.io/":{"body":0,"title":0},"https://alsaaleak.github.io/about/":{"body":3,"title":0},"https://alsaaleak.github.io/posts/":{"body":0,"title":0},"https://alsaaleak.github.io/posts/fsl-fy-l-rdmn/":{"body":0,"title":0},"https://alsaaleak.github.io/posts/hky-hryr-lqs-l-wl/":{"body":3,"title":0},"https://alsaaleak.github.io/posts/jysh-lmsbh/":{"body":0,"title":0},"https://alsaaleak.github.io/posts/khbr-lskyn/":{"body":1,"title":0},"https://alsaaleak.github.io/posts/ktb-ls-lyk/":{"body":1,"title":0},"https://alsaaleak.github.io/posts/lgrf-lhmr/":{"body":0,"title":0},"https://alsaaleak.github.io/posts/mdhkrt-trtbys/":{"body":0,"title":0},"https://alsaaleak.github.io/posts/mfkhr-wyndwz-wllynks/":{"body":0,"title":0},"https://alsaaleak.github.io/posts/mqm-lyl-wldhy-b-bqlm-rby-gyr-rnjy/":{"body":0,"title":0},"https://alsaaleak.github.io/posts/n-l-bn-l-kht/":{"body":0,"title":0},"https://alsaaleak.github.io/posts/nfy-lm-b-n-l-rb-l-wy-l/":{"body":3,"title":0},"https://alsaaleak.github.io/posts/qd-lst-wllm-n/":{"body":6,"title":0},"https://alsaaleak.github.io/posts/qs-by-lthqb/":{"body":0,"title":0},"https://alsaaleak.github.io/webring/":{"body":0,"title":0}},"length":17},"lang":"English"}